function getItems() {
	var blog_id = document.getElementById('blog_id');
	var offset = document.getElementById('offset');
	var autoupdate = document.getElementById('autoupdate');
	var last = document.getElementById('last');
	var button = document.getElementById('button');
	var result = document.getElementById('result');

	var data = {
		blog_id: blog_id.value,
		offset: offset.value,
		last: last.value,
	};

	var ajax = new Ajax('/__ajax__/v1/get_blog_items');
	ajax.setCallback200(function(text){
		var json = JSON.parse(text);
		var items = json['items'];
		var last_item = items.pop();

		if(autoupdate.checked) {
			offset.value = last_item['item_id'];
		}

		result.innerHTML = text;
		console.clear();
		console.log(text);
		console.log(json);
		button.innerHTML = 'POST';
	});
	ajax.query(data);

	button.innerHTML = 'POST ...';
}

